import React, { Component } from 'react';
import settings from "../settings.json";
import { Modal } from 'react-bootstrap';
import { Link, Redirect } from 'react-router-dom';
//import Login from "./components/Login.js";
import JwPagination from 'jw-react-pagination';
import CreatableSelect from 'react-select/lib/Creatable';

import ManageSerie from './ManageSerie';

import '../css/manage.scss';
import '../css/index.scss';
import '../css/createModal.scss';

class Manage extends Component {

	constructor(props){
		super(props);
		document.title = "Hantera serier";

		this.state = {
			data: [],
			loading: true,
			league: false,
			login: true,
			error: false,
			showShadowBottom: {visibility: "hidden"},
			showShadowTop: {visibility: "hidden"},
			status: false
		};

		this.show = this.show.bind(this);
		this.close = this.close.bind(this);
		this.fetchData = this.fetchData.bind(this);

		this.fetchData();
	}

	fetchData() {
		fetch(settings.api + "serie", {
            method: "GET",
            credentials: "include"
        }).then(response => {
			if(!response.ok){console.log(response); throw Error(response.status);}
			return response.json()
		}).then(response => {
			this.setState({
				data: response
			});
			if(response.length > 7){this.setState({showShadowBottom: {visibility: "visible"}});}
			else {this.setState({showShadowBottom: {visibility: "hidden"}});}
			this.setState({loading: false});
			//console.log(response);
		}).catch((error) => {
			console.log(error);
			if({Error: '401'}) {
				this.setState({loading: false, login: false});
			}
			else {
				this.setState({loading: false, error: true});
			}
		});
	}

	show(){
			this.setState({status: true});
	}

	close(){
		this.setState({status: false});
		this.fetchData();
	}

/*---------------------------RENDER--------------------------*/

	render() {
		if(this.props.match.params.id) {
			return (
				<div>
					<ManageSerie id={this.props.match.params.id} />
				</div>
			);
		}

		else {
			let content = "";
			let serier = [];
			let owner = [];

			owner = this.state.data.map((item) => {
				if(item.owners.length === 1) {
					return "-";
				}
				else if(item.owners.length === 2) {
					return "Hanteras med en annan användare."
				}
				else {
					return "Hanteras av dig och " + (item.owners.length - 1) + " andra användare.";
				}
			});

			if(!this.state.data.length) {
				serier = (
					<div className="skapaNy" onClick={this.show}>
							<div className="skapaNyTitel">
