import React, { Component } from 'react';
import settings from "../settings.json";
import { Link, Redirect } from 'react-router-dom';
import { Modal } from 'react-bootstrap';


import FutureGames from './ManageTourComps/FutureGames';
import Group from './Group';

import '../css/managetour.scss';
import '../css/bootstrap.css';

class ManageTour extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tourData: this.props.data,
      teams: this.props.data.teams,
      groups: this.props.data.groups,
      pointerFollowStyle: {left: '0px'},
      groupSelected: this.props.groupSelected,
      showShadowBottom: {visibility: "visible"},
			showShadowTop: {visibility: "visible"},
      showPointer: true,
      groupClass: 'grupperSel',
      login: true,
      error: false,
      firstLoad: true,
      groupHeight: {height: '70px'},
      lastScrollY: 0,
      ticking: false,
      showModal: false
		};

    this.format = "ABCDEFGHIJKLMNOP";
    this.ref = React.createRef();

    this.handlegroupchange = this.handlegroupchange.bind(this);
    this.resize = this.resize.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleShow = this.handleShow.bind(this);
  }

  componentWillMount(){
    window.addEventListener('resize', this.resize);
  }

  componentWillUnmount(){
    window.removeEventListener('resize', this.resize);
  }

  componentDidMount() {
    this.resize();
  }

  resize(){
    const offset = (document.querySelector(".grupperSel").offsetWidth)/2;
    const boxStart = (document.querySelector(".grupperSel").childNodes[0].getBoundingClientRect().x)
                    - (document.querySelector(".groupWrap").getBoundingClientRect().x);
    this.setState({
      pointerFollowStyle: {
        left:
          boxStart + offset +'px'
      }
    });
  }

  componentDidUpdate() {
    if(!this.state.loading) {
      if(this.state.firstLoad) {
        this.resize();
        this.setState({firstLoad: false});
      }
    }
  }

  handleClose() {
    this.setState({ showModal: false });
  }

  handleShow() {
    this.setState({ showModal: true });
  }

  handlegroupchange(event) {
    if(event.currentTarget != event.currentTarget.parentNode.childNodes[this.state.groupSelected]) {
      this.setState({groupSelected: parseInt(event.currentTarget.dataset.id)});

      this.setState({groupClass: 'grupper'});
      event.currentTarget.parentNode.childNodes[this.state.groupSelected].className = 'grupper';

      this.setState({showPointer: true});
      event.currentTarget.className = 'grupperSel';

      this.resize();
    }
  }

  componentWillReceiveProps(input) {
    console.log('update');
    this.setState({
      tourData: input.data,
      teams: input.data.teams,
      groups: input.data.groups//,
      //groupSelected: input.groupSelected
    });
  }

	render() {
    if(this.state.error) {
			document.title = "Ingen turnering hittades";
			return(
  			<div>
  				<div className="no-tournament">
  					<h1>Ingen turnering hittades</h1>
  					<p>Vänligen kontrollera att du angav korrekt address.</p>
  					<div className="link">&#128279; = &#128148;</div>
  					<div className="line"></div>
  				</div>
  			</div>
			);
		}

    else {
      let urlString;
      let popup;
      let groups;
      let rubrik;
      let pointer = "";
      let resultatClass;
      let displayedGroup;
      let edit = "";

      if(this.state.loading) {
        let inside = (
          <div className="loading"></div>
        );
      }

      else {
        // groups = this.state.groups.map((item, index, array) => {
        //   let completed = "";
        //   if(item.completed) { completed =  <div className="groupDoneIndi">&#10004;</div>; }
        //   if(index == this.state.groupSelected) {
        //     return (
        //         <div className="grupperSel" key={index} data-id={index} onClick={this.handlegroupchange}>
        //           <span style={{position: 'absolute', left: '-20px'}}/>
        //           Serie {index + 1}
        //           {completed}
        //         </div>
        //     )
        //   }
        //   else {
        //     return (
        //         <div className="grupper" key={index} data-id={index} onClick={this.handlegroupchange}>
        //           <span style={{position: 'absolute', left: '-20px'}}/>
        //           Serie {index + 1}
        //           {completed}
        //         </div>
        //     )
        //   }
        // });

        edit = (
          <div className="editContainer">
            <div id="futureGamesWrap">
              <FutureGames
                data={this.state.tourData}
                groupSelected={this.state.groupSelected}
                fetchData={this.props.fetchData}
                bracket={false}
              />
            </div>
            <div className="tabellwrap">
              <div className="tabellcontent">
                <Group data={this.state.tourData.groups[this.state.groupSelected]} teams={this.state.tourData.teams}></Group>
              </div>
            </div>
          </div>
        );

        rubrik = (
          <div className="rubrik">
            <h2>{this.state.tourData.name}</h2>
          </div>
        );

        if(this.state.showPointer) {
          pointer = [
            <div className="pointer-a" key="a" style={this.state.pointerFollowStyle}></div>,
            <div className="pointer-b" key="b" style={this.state.pointerFollowStyle}></div>
          ];
        }
        resultatClass = 'editWrap';

        urlString = "http://localhost:3000/t/" + this.state.tourData.id;
      }

      return (
        <div id="manageTour" ref={this.ref}>
          <div className="groupWrap" style={this.state.groupHeight}>
            <div className="groupContent">
              {groups}
            </div>
            <div className="clear"></div>
            <div className="pointerWrap">
              <div className="pointerContainer">
                {pointer}
              </div>
            </div>
          </div>
          <div className="clear"></div>
          <div className={resultatClass}>
            {edit}
          </div>
        </div>
      );
    }
	}
}

export default ManageTour;

